import axios from "axios";
export const url = "http://localhost:2000/posts";
export const fetchPosts = () => axios.get(url);
const config = {
  headers: {
    "Content-Type": "application/json",
  },
};
export const createPost = (newPost) => axios.post(url, newPost, config);
